package myFirstJavaSession;

public class myJavaCodes2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int $num = 10;
		int x = 123;
		
		int num123 = 123;
		
		int NUM = 19;
		
		System.out.println(NUM);
		
		int Public = 10;
		
		// we call names = identifiers in java
		// names should make sense
		
		int thisisalongname = 10; // not a good way to define long names
		int this_is_a_long_name = 10; // Separate the word with underscore
		int thisIsALongName = 10; // you can also camel-case the name
		
		// we always start variables, methods, and package names in lower case
		// we always start class names with upper case
		// we have exceptions to above rules ^
		
		
		// creating variables
		// data-type name/identifier;    <=== Declaration
		int nameOfVariable;
		// name = value;   <==== Initialization
		nameOfVariable = 555;
		// data-type name = value;   <=== this is both Declaration and Initialization
		int name2 = 666;
		
//		Declaration
		// reserving the name of the variable
		
//		Initialization
		// is assigning value to the variable for the first time
		
		// Assignment operator is the = sign
		// with this operator, you are assigning the value to the right to the variable on the left
		name2 = 4;
		
		// Initialization vs. Assignment
		// Initialization is the first time that you assign a value
		// Assignment is any time after the first time that you change the value
		
	}

}
